<template>
<div>  
<div class="app-aside" :class="aside ? 'active-aside':''">
    <ul class="aside-list">
      <li>
        <button @click="aside=!aside">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" >
          <path d="M4 18H20M4 6H20H4ZM4 12H20H4Z" stroke="#A7B1BD" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
        </svg>
        </button>
        <img src="@/assets/img/logo.svg" class="ml-4">
        <bell :badge="true" class="ml-auto" />
      </li>
      <router-link :to="item.path" tag="li"  v-for="item in list" :key="item.svg">
        <svg  width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" v-html="item.svg" ></svg>
        <span>{{ item.name }}</span>
      </router-link>
    </ul>
  </div>
  </div>

</template>
<style type="text/css" lang="scss" scoped="">
$icon_padding: 1rem;

.app-aside {
  min-width: 55px;
  width: 55px;
  min-height: 100vh;
  border-right: 1px solid #D3D8DE;
  background-color: #FBFDFF;
  overflow: hidden;
  transition: all 0.1s ease-out;
  .aside-list {
    min-width: 220px;
    li {
      &:first-child {
        height: 64px;
      }
      &:not(:first-child).router-link-active {
        background-color: #DFEEFF;
        color: #007AFF;
        &::before {
          width: 4px;
          height: 100%;
          padding: 0;
          position: absolute;
          left: 0;
          content: '';
          background: #007AFF;
          border-radius: 0px 3px 3px 0px;
        }
        svg {
          stroke: #007AFF;
        }
      }
      &:not(:first-child):hover {
        background-color: #DFEEFF;
        color: #007AFF;
        svg {
          stroke: #007AFF;
        }
      }
      width: 100%;
      height: 56px;
      position: relative;
      // transition: all 0.2s ease-out;
      padding-left: $icon_padding;
      padding-right: $icon_padding;
      display: flex;
      align-items: center;
      cursor: pointer;
      border-bottom: 1px solid #D3D8DE;
      color: #091C31;
      user-select: none;
      svg {
        stroke: #A7B1BD;
      }
      span {
        margin-left: $icon_padding;
        font-family: SF Pro Display;
        font-style: normal;
        font-weight: 500;
        font-size: 15px;
        line-height: 20px;
        display: flex;
        align-items: center;
      }
    }
  }
}
.active-aside{
  width: 220px !important;
}
</style>

